import styled, { css } from 'styled-components';

export const AppContainer = styled.div`
    border: 1px solid red;
    height: 100%;
    width: 100vw;

    display: flex;
    flex-flow: column wrap;
`;

export const PageContainer = styled.div`
    border: 1px solid blue;
    height: 300vh;
    width: 100vw;

    ${(props) => 
        props.custom &&
        css`
            height: ${props.height}
        `
    }
`;
